<?phpadd_action('admin_menu', 'cp_acl_settings_page_add');function cp_acl_settings_page_add(){    add_options_page(        $page_title = 'Настройки ACL',        $menu_title = 'Настройки ACL',        $capability = 'manage_options',        $menu_slug  = 'cp_acl_settings_page',        $function   = 'cp_acl_settings_page_function');}function cp_acl_settings_page_function(){?>    <div class="wrap">        <h1>Настройки ACL</h1>        <form action="options.php" method="POST">            <?php settings_fields( 'cp_acl_settings_page' ); ?>            <?php do_settings_sections( 'cp_acl_settings_page' ); ?>            <?php submit_button(); ?>        </form>    </div><?php}add_action('admin_init', 'cp_acl_init_options');function cp_acl_init_options(){    register_setting( 'cp_acl_settings_page', 'cp_acl_posts_types' );    add_settings_section(        $id         = 'cp_acl_settings_sections',        $title      = '',        $callback   = 'cp_settings_pages_section_callback',        $page       = 'cp_acl_settings_page'    );    add_settings_field(        $id         = 'cp_acl_options_posts_types',        $title      = 'Типы записей',        $callback   = 'cp_acl_options_posts_types_callback',        $page       = 'cp_acl_settings_page',        $section    = 'cp_acl_settings_sections'    );}function cp_settings_pages_section_callback(){    ?>    <p>Укажите типы записей, на которые будут распространяться правила ACL</p><?php}function cp_acl_options_posts_types_callback(){    $setting_name = 'cp_acl_posts_types';    $setting_value = get_option( $setting_name );    ?>    <div id="<?php echo $setting_name; ?>">        <!--<input type="text" size="55" name="<?php echo $setting_name; ?>" value="<?php echo $setting_value; ?>" />-->        <select style="width:300px; height:300px;" multiple="multiple" name="<?php echo $setting_name; ?>[]">            <?php            $post_types = get_post_types('', 'objects');            foreach ($post_types as $post_type ): ?>                <option<?php if(in_array($post_type->name, $setting_value)) echo ' selected="selected"'; ?> value="<?php echo $post_type->name; ?>"><?php echo $post_type->name; ?></option>           <?php  endforeach;  ?>        </select>    </div><?php}